// import { Button, Flex, FormControl, Heading, Stack, VStack } from '@chakra-ui/react'
import { Button, Flex, VStack, FormControl, Heading, Stack, FormLabel, Input} from '@chakra-ui/react'
import {React, useEffect, useState} from 'react'
import {Login} from '../components'

const initialUser = {
  email: '', 
  name:'', 
  password:'',
  isMember: true
}

const LoginP = () => {
  // for user
    const [user, setUser] = useState(initialUser)
    const handleUser =(e) => {
      setUser({...user, [e.target.name]:e.target.value})
    }
    // toggle between login and user page
    const toggleMember = ()=>{
      setUser({...user,
      isMember: (!user.isMember)})
    }
    useEffect(() => {
      console.log(user)
    })
// for passwords
    const [show, setShow] = useState(false)
    const handleShow = () => setShow(!show)
  return (

    <Flex alignItems={'flexed-top'} marginTop='7%' justifyContent={'center'}>
    <VStack>
    <FormControl>
      {/* heading  */}
      <Heading textAlign={'center'}>{user.isMember ? "Login" : "Register"}</Heading>
      
      {/* name for registration */}
      <Stack marginTop={'2%'}>
        {(!user.isMember) && (
        <>
          <FormLabel>Name</FormLabel>
          <Input name='name' type='text' value={user.name} onChange={handleUser}/>
        </>
            )}
      </Stack>
            

      {/* email */}
      <Stack  marginTop={'3%'}>
          <FormLabel>Email address</FormLabel>
          <Input type='email' 
              value={user.email}
              // onChange={handleUserEmail()}
              />
      </Stack>
      {/* password */}
      <Stack marginTop={'5%'}>
          
          <FormLabel>Password</FormLabel>
          <InputGroup size='md'>
              <Input
                  pr='4.5rem'
                  type={show ? 'text' : 'password'}
                  placeholder='Enter password'
                  value={user.password}
                  // onChange={handleUserPassword()}
              />
              <InputRightElement width='4.5rem'>
                  <Button h='1.75rem' size='sm' onClick={handleShow}>
                  {show ? 'Hide' : 'Show'}
                  </Button>
              </InputRightElement>
          </InputGroup>
      </Stack>
      {/* Submit */}
      <Button colorScheme='orange' marginTop={'2%'}>Submit</Button>
      
      {/* Toggle button */}
      <Button colorScheme={'teal'} variant='ghost' float={'right'} marginTop='2%' onClick={toggleMember}>
          {!user.isMember ? 'Already a member? Login Here' : 'Not a member yet? Register Here'}
      </Button>
    </FormControl>
    </VStack>
  </Flex>
  )
}

export default LoginP
