// import { Button, Flex, FormControl, Heading, Stack, VStack } from '@chakra-ui/react'
import { Button, Flex, VStack, FormControl, Heading, Stack} from '@chakra-ui/react'
import {React, useEffect, useState} from 'react'
import {Login, Register} from '../components'

const initialUser = {
  email: '', 
  name:'', 
  password:'',
  isMember: true
}

const LoginP = () => {
    const [user, setUser] = useState(initialUser)
    const handleUser =(e) => {
      setUser({...user, [e.target.name]:e.target.value})
    }
    const toggleMember = ()=>{
      setUser({...user,
      isMember: (!user.isMember)})
    }
    useEffect(() => {
      console.log(user)
    })
  return (

    <Flex alignItems={'flexed-top'} marginTop='7%' justifyContent={'center'}>
    <VStack>
    <FormControl>
      {/* heading  */}
      <Heading textAlign={'center'}>{user.isMember ? "Login" : "Register"}</Heading>
      
      {/* name for registration */}
      <Stack marginTop={'2%'}>
        {(!user.isMember) && (<>
          <FormLabel>Name</FormLabel>
          <Input type='text' 
              value={user.name}
              onChange={handleUser()}/></>
            )}
      </Stack>
            

      <Login user={user} handleUser={handleUser} />
      
      <Button colorScheme={'teal'} variant='ghost' float={'right'} marginTop='2%' onClick={toggleMember}>
          {!user.isMember ? 'Already a member? Login Here' : 'Not a member yet? Register Here'}
      </Button>
    </FormControl>
    </VStack>
  </Flex>
  )
}

export default LoginP
