// import { Button, Flex, FormControl, Heading, Stack, VStack } from '@chakra-ui/react'
import { Button, Flex, VStack, FormControl, Heading, Stack, FormLabel, Input, InputGroup, InputRightElement} from '@chakra-ui/react'
import {React, useEffect, useState} from 'react'
import { signOnService } from '../services/login'
import {useNavigate} from 'react-router-dom'
import AlertItem from '../components/Alert'

const initialUser = {
  email: '', 
  name:'', 
  password:'',
  isMember: true
}



const LoginP = () => {
  // for user
    const navigate = useNavigate()
    const [user, setUser] = useState(initialUser)
    const handleUser =(e) => {
      setUser({...user, [e.target.name]:e.target.value})
    }
    // toggle between login and user page
    const toggleMember = ()=>{
      setUser({
      isMember: (!user.isMember)})
    }



    // alert variable
    const [alert, setAlert] = useState({
      showAlert:false,
      msg: '', 
      heading: '', 
      type: ''})
    // const displayAlert = (newAlert) => {
    //   setAlert({...alert, newAlert})
    // }
 //submit
    const handleSubmit= async (e) => {
      e.preventDefault()
      const response = await signOnService(user)

      

      if(response.status === 200)
      

      

      setTimeout(() => {
        navigate('/dashboard')
      }, 3000);
      else {
        
          console.log(response.data)
      }
    }
// for passwords
    const [show, setShow] = useState(false)
    const handleShow = () => setShow(!show)
  return (

    <Flex alignItems={'flexed-top'} marginTop='7%' justifyContent={'center'}>
    <VStack>
    <FormControl>
      {/* heading  */}
      <Heading textAlign={'center'}>{user.isMember ? "Login" : "Register"}</Heading>
      
      {/* alert */}
      {alert.showAlert && <AlertItem alert={alert} defaultTitle='Invalid Credentials' marginTop='2%' />}
      
      {/* name for registration */}
      <Stack marginTop={'2%'}>
        {(!user.isMember) && (
        <>
          <FormLabel>Name</FormLabel>
          <Input name='name' type='text' value={user.name} onChange={handleUser}/>
        </>
            )}
      </Stack>
            

      {/* email */}
      <Stack  marginTop={'3%'}>
          <FormLabel>Email address</FormLabel>
          <Input name='email' type='email' value={user.email} onChange={handleUser}/>
      </Stack>
      

      {/* password */}
      <Stack marginTop={'5%'}>
          
          <FormLabel>Password</FormLabel>
          <InputGroup size='md'>
              <Input
                  name='password'
                  pr='4.5rem'
                  type={show ? 'text' : 'password'}
                  placeholder='Enter password'
                  value={user.password}
                  onChange={handleUser}
              />
              <InputRightElement width='4.5rem'>
                  <Button h='1.75rem' size='sm' onClick={handleShow}>
                  {show ? 'Hide' : 'Show'}
                  </Button>
              </InputRightElement>
          </InputGroup>
      </Stack>


      {/* Submit */}
      <Button colorScheme='orange' marginTop={'2%'} onClick={handleSubmit}>Submit</Button>
      

      {/* Toggle button */}
      <Button colorScheme={'teal'} variant='ghost' float={'right'} marginTop='2%' onClick={toggleMember}>
          {!user.isMember ? 'Already a member? Login Here' : 'Not a member yet? Register Here'}
      </Button>
    </FormControl>
    </VStack>
  </Flex>
  )
}

export default LoginP
