import axios from 'axios'
const uri = 'http://localhost:5000/api/v1/auth'

export const signOnService = async user => {
    const route = user.isMember ? `${uri}/login` : `${uri}/register`
    console.log("THe route is: ", route)
    console.log(user.isMember)
    try {
        return await axios.post(route, user,{
                withCredentials:true,
            })    
    } catch (error) {
        return null
    }
}

export const getAuthenticateUser = async () => {
    const route = `${uri}/getUser`
    const response = await axios.post(route,{withCredentials:true,})
    return response;
}
