// import { Button, Flex, FormControl, Heading, Stack, VStack } from '@chakra-ui/react'
import { Button, Flex, VStack, } from '@chakra-ui/react'
import {React, useState} from 'react'
// import {Login, Register} from '../components'
const LoginP = () => {
    const [isMember, setMember] = useState(0)
    const initialUser = {email: '', 
    name:'', 
    password:''}
    const [user, setUser] = useState(initialUser)
    const handleUser =(e) => {
      setUser()
    }
    
  return (

    <Flex alignItems={'flexed-top'} marginTop='7%' justifyContent={'center'}>
    <VStack>
    <FormControl>
      {/* heading  */}
      <Heading textAlign={'center'}>{isMember ? "Login" : "Register"}</Heading>
      
      {/* name for registration */}
      <Stack marginTop={'2%'}>
        {(!isMember) && <Register user={user} handleUser={handleUser} />}
      </Stack>
            

      <Login user={user} handleUser={handleUser} />
      
      <Button colorScheme={'teal'} variant='ghost' float={'right'} marginTop='2%' onClick={toggleMember}>
          {!isMember ? 'Already a member? Login Here' : 'Not a member yet? Register Here'}
      </Button>
    </FormControl>
    </VStack>
  </Flex>
  )
}

export default LoginP
