import React from 'react'
import {
    Alert,
    AlertIcon,
    AlertTitle,
    AlertDescription,
    Box,
  } from '@chakra-ui/react'

const AlertItem = (alert, defaultHeading) => {
  return (
    <Alert status={alert.type}>
      <AlertIcon />
      <Box>
        <AlertTitle>{alert.heading || defaultHeading}</AlertTitle>
        <AlertDescription>
          {alert.msg}
        </AlertDescription>
      </Box>
      <CloseButton
        alignSelf='flex-start'
        position='relative'
        right={-1}
        top={-1}
        onClick={onClose}
      />
    </Alert>
  )
}

export default AlertItem
